const birthday = new Date('10.03.1987');

const data = {
    name: 'максим лескин',
    dateBirth: birthday ,
    purpose: 'карьерный рост'
};

export const dataBirth = birthday.toLocaleDateString();

export const words = data.name.split(' ');
export const firstName = words[0][0].toUpperCase() + words[0].substring(1);
export const lastName = words[1][0].toUpperCase() + words[1].substring(1);
export const age = Math.floor ((new Date() - birthday) / 31556952000 );
export const purposes = data.purpose[0].toUpperCase() + data.purpose.substring(1);

export class Person {
    constructor () {
    this.id = Math.random().toString(36).substring(3);
    this.firstName =  firstName;
    this.lastName = lastName;
    this.dataBirth = dataBirth;
    this.age = age;
    this.purpose = purposes;
  }
};

